{
    "query": "PREFIX ex: <http://www.example.org/ebook#>

SELECT * WHERE {
    ?author ex:name "J.K. Rowling" .
    ?book a ex:Ebook .
    ?book ex:writtenBy ?author .
    ?book ex:title ?title .
    OPTIONAL { ?book ex:isbn ?isbn . }
    OPTIONAL { ?book ex:publicationYear ?publicationYear . }
    OPTIONAL { ?book ex:publishedBy ?publisher . }
    OPTIONAL { ?book ex:belongsToGenre ?genre . }
    OPTIONAL { ?genre ex:label ?label . }
    OPTIONAL { ?author ex:name ?authorName . }
    OPTIONAL { ?publisher ex:label ?publisherName . }
    OPTIONAL { ?genre ex:label ?genreName . }
}"
}
